CREATE DEFINER=`root`@`localhost` PROCEDURE `spMakaleKaydet`(
kategori_isim varchar(15),
yazar_isim varchar(100),
gazete_isim varchar(15),
baslik varchar(200),
icerik longtext,
tarih varchar(45),
rLink varchar(300),
kategori_id int,
yazar_id int,
gazete_id int,
makale_id int)
BEGIN
	declare varmi int;

	set @varmi = (select count(*) from kategoriler where kategoriler.isim = kategori_isim);
	if(@varmi = 0) then
	insert into kategoriler(isim) values (kategori_isim);
	set kategori_id=LAST_INSERT_ID();
	else
	set kategori_id = (select id from kategoriler where kategoriler.isim = kategori_isim);
	end if;
    
	set @varmi = (select count(*) from gazeteler where gazeteler.isim = gazete_isim);
	if(@varmi = 0) then
	insert into gazeteler(isim) values (gazete_isim);
	set gazete_id=LAST_INSERT_ID();
	else
	set gazete_id = (select id from gazeteler where gazeteler.isim = gazete_isim);
	end if;
    
	set @varmi = (select count(*) from yazarlar where yazarlar.isim = yazar_isim and yazarlar.resimLink = rLink);
	if(@varmi = 0) then
	insert into yazarlar(isim, resimLink) values (yazar_isim, rLink);
	set yazar_id=LAST_INSERT_ID();
	else
	set yazar_id = (select id from yazarlar where yazarlar.isim = yazar_isim and yazarlar.resimLink = rLink);
	end if;

	set @varmi = (select count(*) from makaleler where makaleler.baslik = baslik and makaleler.icerik = icerik);
    if(@varmi = 0) then
	insert into makaleler(id,gazete_id,yazar_id,kategori_id,baslik,icerik,tarih) values (null,gazete_id, yazar_id, kategori_id, baslik, icerik, tarih);
    set makale_id = LAST_INSERT_ID();
    else
    set makale_id = (select id from makaleler where makaleler.gazete_id = gazete_id and makaleler.yazar_id = yazar_id and makaleler.kategori_id = kategori_id and makaleler.baslik = baslik and makaleler.icerik = icerik and makaleler.tarih = tarih);
    end if;
END ;;
